import React from 'react';
import video from "./CodeSandbox.mov";
// import image from "./DSC.png";
import image2 from "./playbutton.svg";




class Video extends React.Component {
    constructor(props){
        super(props);
       this.buttonRef=React.createRef();
        this.wrapperRef=React.createRef();
    }

  videoPlay=()=>{
// this.buttonRef.style.dysplay="none";
console.log(this.buttonRef);
      console.log(this.wrapperRef);

    }

    render() {
        return (
            <div className={"videoWrapper"} ref={this.wrapperRef} onClick={this.videoPlay}>
                <div className="videoButton" onClick={this.videoPlay} ref={this.buttonRef} >
                    <img src={image2} alt="foto"/>
                    </div>
                <video controls loop muted>
                    <source src={video} type="video/mp4"/>
                </video>
            </div>
        );
    }

}

export default Video;
