import React from "react";
import {TEXT} from "../translate";


const withApp=(Component)=>{
   class HOC extends React.Component{
   constructor(props){
      super(props);
      this.state={
          language:TEXT.RU
      }
       console.log(this.state.language.FOOTER)
   }


   render(){
       return(
           <Component delegate={this}/>
       )
   }
   }
   return HOC;
}


export default withApp;
