import React from 'react';
import video from "./CodeSandbox.mov";
// import image from "./DSC.png";
import image2 from "./playbutton.svg";


class Video extends React.Component {
    constructor(props) {
        super(props);
        this.buttonRef = React.createRef();

    }

    videoPlay = () => {


        console.log(this.buttonRef);

        this.buttonRef.current.style.display="none";
        console.log(this.buttonRef.current.nodeName)
    }

    render() {

        return (
            <React.Fragment  >

                <video controls loop muted>
                    <source src={video} type="video/mp4"/>
                </video>
                <button className="videoButton" onClick={this.videoPlay} ref={this.buttonRef}>
                    <img src={image2} alt="foto"/>
                </button>
            </React.Fragment>
        );
    }

}

export default Video;
