import React from "react";
import image from "./Images/Lang.png";
import withApp from "../../../../HOCs/withApp";
import {LangContext} from "../../../../index";


const SelectLanguage=(props)=>{

    const changeLang=(e)=>{
        LangContext.value=e.target.value;
    }
    console.log(LangContext.value)
    return(
        <div className={"lang"}>
            <img src={image} alt={props.title}/>
            <select name="languages" id="languages" onChange={changeLang}>
                <option value="ru" >RU</option>
                <option value="en" >EN</option>
            </select>
        </div>
    )
}

export  default withApp(SelectLanguage);
